import React from 'react';
import "./list.css";

export default function List ({tarefas, handleDelete, active, tarefasFeitas, isActive, checked, dragging, onDrop, dragStart}){

        // e.target.classList.toggle('strikeWord')
    if (tarefas.length === 0 && isActive === true) {
        return (
            <div className="tarefas">Não Há nenhuma tarefa ainda</div>
            
        );
    } else if (tarefasFeitas.length === 0 && isActive === false) {
        return (
            <div className="tarefas">Não Há nenhuma tarefa feita ainda</div>
        );
    } else {
            return (
                
                <div>
                    <ul className="tarefasLista">
                        {tarefas.map((tarefa, index) => (
                        <li id="dragtarget" draggable='true' onDragOver={(e) => allowDrop(e)} onDragStart={(e) => dragStart(e)} onDrop={(e) => onDrop(e)} onDrag={(e) => dragging(e)} className={`formText`} key={tarefa}>
                            <input checked={checked[index]} onChange={(e) => active(e, tarefa, index)} className='checkbox' type="checkbox" id="checkbox"/>
                            <div className={`divTarefa ${checked[index] ? 'strikeWord' : ''}`}>{tarefa}</div>
                            <button className='deleteButton' onClick={(e) => handleDelete(e, index)}>
                                <img src="./images/icon-cross.svg" alt='icon-cross'/>
                            </button>
                        </li>
                        ))}
                    </ul>
                </div>
        );
    }
}
