import React from 'react';

import "./list.css";

export default function List ({tarefas, handleDelete }){

    const [isActive, setIsActive] = React.useState(false);

    const handleClick = (tarefa, index) => {
        console.log(tarefa)
        if (tarefas[index] == tarefa) {
            setIsActive(current => !current);        
        }
    }


    if (!tarefas.length) {
        return (
            <div className="tarefas">Não Há nenhuma tarefa ainda</div>
        );
    } else {
            return (
                <div>
                    <ul className="tarefasLista">
                        {tarefas.map((tarefa, index) => (
                        <li className={`${isActive ? 'strikeWord' : ''} formText`} key={tarefa}>
                            <input onClick={handleClick(tarefa, index)} className='checkbox' type="checkbox" />{tarefa}
                            <button className='deleteButton' onClick={(e) => handleDelete(e, index)}>
                                <img src="./images/icon-cross.svg" alt='icon-cross'/>
                            </button>
                        </li>
                        ))}
                    </ul>
                </div>
        );
    }
}
