import React from 'react';

export default function List ({tarefas, handleDelete}){
        return (
        <ul className='tarefa'>
            {tarefas.map((tarefa, index) => (<li key={tarefa}><input type="checkbox" />
            {tarefa} 
            <button onClick={(e) => handleDelete(e, index)}>
                <img src="./images/icon-cross.svg" />
            </button></li>))}
        </ul>     
    );
}
