import React from 'react';

import "./list.css";

export default function List ({tarefas, handleDelete, }){

    const [isActive, setIsActive] = React.useState(false);

    const handleClick = (index) => {
        console.log(tarefa)
        let classes = 'strikeWord'
        let els = document.getElementsByClassName('strikeWord active');
        let lists = document.getElementsByClassName('lists')[index]
        if(els){
            while (els[0]) {
                els[0].classList.remove('active')
            }
        }
        
        lists.className = classes.replace('strikeWord', 'strikeWord active')
    }


    if (!tarefas.length) {
        return (
            <div className="tarefas">Não Há nenhuma tarefa ainda</div>
        );
    } else {
            return (
                <div>
                    <ul className="tarefasLista">
                        {tarefas.map((tarefa, index) => (
                        <li className={`lists formText`} key={tarefa}>
                            <input onClick={() => handleClick(index)} className='checkbox' type="checkbox" />{tarefa}
                            <button className='deleteButton' onClick={(e) => handleDelete(e, index)}>
                                <img src="./images/icon-cross.svg" alt='icon-cross'/>
                            </button>
                        </li>
                        ))}
                    </ul>
                </div>
        );
    }
}
