import React from 'react';

import "./list.css"

export default function List ({tarefas, handleDelete}){
        return (
        <ul className='tarefa'>
            {tarefas.map((tarefa, index) => (
            <li key={tarefa}><input type="checkbox" />{tarefa} 
                <button onClick={(e) => handleDelete(e, index)}>
                    <img src="./images/icon-cross.svg" alt='icon-cross'/>
                </button>
            </li>))}
        </ul>     
    );
}
