import React from 'react';
import "./list.css";

export default function List ({tarefas, handleDelete, active, tarefasFeitas, isActive}){

        // e.target.classList.toggle('strikeWord')
    if (tarefas.length === 0 && isActive === false) {
        return (
            <div className="tarefas">Não Há nenhuma tarefa ainda</div>
            
        );
    } 
    
    if (tarefasFeitas.length === 0 && isActive === false) {
        return (
            <div className="tarefas">Não Há nenhuma tarefa feita ainda</div>
        );
    } 
    
    if(tarefas.length > 0) {
            return (
                <div>
                    <ul className="tarefasLista">
                        {tarefas.map((tarefa, index) => (
                        <li className={`formText`} key={tarefa}>
                            <input onClick={(e) => active(e, tarefa, index)} className='checkbox' type="checkbox" id="checkbox"/>
                            <div className='divTarefa'>{tarefa}</div>
                            <button className='deleteButton' onClick={(e) => handleDelete(e, index)}>
                                <img src="./images/icon-cross.svg" alt='icon-cross'/>
                            </button>
                        </li>
                        ))}
                    </ul>
                </div>
        );
    }
}
