import React from "react";
import './form.css'

export default function Form ({handleSubmit, handleChange, novaTarefa}) {
    function singleActive (e) {
        const mainInput = document.getElementById('MainInput')
        mainInput.id
    }
    return (
    <form onSubmit={handleSubmit} action='#'>
        <input onClick={(e) => singleActive(e)} className="checkbox" type="checkbox" />
        <input id="mainInput" className="formText" onChange={handleChange} type="text" value={novaTarefa}/>
    </form>
    )
}
