import React from 'react'
import "./footer.css"
export default function Footer({items, all, active, completed, clear }) {
    if(!items.length) {
        return (
            <div >Não Há nenhuma tarefa feita ainda</div>
        )
    }
    return (
        <footer className='footer'>
            
            <div>
                <div>{items} Items left</div>
            </div>
            <ul>
                <li onClick={all}>All</li>
                <li onClick={active}>Active</li>
                <li onClick={(e) => completed(e)}>Completed</li>
            </ul>
            <div className='buttonClear' onClick={clear}>Clear Completed</div>
        </footer>
    );
}
