import React, { Component } from 'react';
import DarkMode from './darkMode';
export default class Header extends Component {
    state = {
        novaTarefa: '',
        tarefas: []
    }
    componentDidMount() {
        
    }
    componentDidUpdate(Prevprops, prevState) {
        const {tarefas} = this.state;

        if (tarefas === prevState.tarefas) return
    }
    handleSubmit = (e) => {
        e.preventDefault();
        const { tarefas } = this.state;
        let { novaTarefa } = this.state;

        novaTarefa = novaTarefa.trim();

        if(tarefas.indexOf(novaTarefa) !== -1) return;
        const novasTarefas = [...tarefas];

        this.setState({
            tarefas: [ ...novasTarefas, novaTarefa],
        })
    }

    handleChange = (e) => {
        this.setState({
            novaTarefa: e.target.value,
        })
    }

    handleDelete(e, index) {
        const { tarefas } = this.state;
        const novasTarefas = [...tarefas];
        novasTarefas.splice(index, 1)

        this.setState({
            tarefas: [...novasTarefas],
        })
    }

    // const [stateSend, setStateSend] = useState()
    render() {
        const { novaTarefa, tarefas } = this.state;

        return (
            <div className='header'>
                <div className='bg-header'>
                    <h1>TODO</h1>
                    <DarkMode darkMode={false}></DarkMode>
                </div>
                <form onSubmit={this.handleSubmit} action='#'>
                    <input onChange={this.handleChange} type="text" value={novaTarefa}/>
                    <button type="submit">Send</button>
                </form>

                <ul className='tarefa'>
                     {tarefas.map((tarefa, index) => (<li key={tarefa}><input type="checkbox" /> {tarefa} <button onClick={(e) => this.handleDelete(e, index)}><img src="./images/icon-cross.svg" /></button></li>))}
                </ul>      
            </div>

            
        );
    }
}
