import React from "react";
import './form.css'

export default function Form ({handleSubmit, handleChange, novaTarefa, active}) {
    return (
    <form onSubmit={handleSubmit} action='#'>
        <input onClick={() => active()} className="checkbox" type="checkbox" />
        <input className="formText" onChange={handleChange} type="text" value={novaTarefa}/>
    </form>
    )
}
