import React from 'react';

export default class List extends React.Component {
    constructor(props) {
        super(props);

        this.state = props.tarafa;
    }
    
    render() {
        return (
            <ul className='tarefa'>
                    {this.state.tarefas.map(tarefa => (<li key={tarefa}>{tarefa}</li>))}
            </ul>        
        );
    }
}
