import React from 'react';

export default class List extends React.Component {
    constructor(props) {
        super(props);

        this.state = {tarefas: this.props};
    }
    
    render() {
        let tarefas = this.state
        return (
            <ul className='tarefa'>
                    {tarefas.map(tarefa => (<li key={tarefa}>{tarefa}</li>))}
            </ul>        
        );
    }
}
